package com.example.simpletouristapp.ui.home;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.simpletouristapp.adapter.PopularPlaceAdapter;
import com.example.simpletouristapp.databinding.FragmentHomeBinding;
import com.example.simpletouristapp.model.PopularPlaces;

import java.util.ArrayList;
import java.util.List;

public class HomeFragment extends Fragment {

    private FragmentHomeBinding binding;
    private List<PopularPlaces> popularPlaces;
    private RecyclerView recyclerView;

    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {
//        HomeViewModel homeViewModel =
//                new ViewModelProvider(this).get(HomeViewModel.class);

        binding = FragmentHomeBinding.inflate(inflater, container, false);
        View root = binding.getRoot();

//        final TextView textView = binding.textHome;
//        homeViewModel.getText().observe(getViewLifecycleOwner(), textView::setText);
        return root;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        recyclerView = binding.rvItemPopularPlaces;
        popularPlaces = new ArrayList<>();
        popularPlaces.add(new PopularPlaces("TP HCM","https://meta.vn/Data/image/2022/01/13/anh-dep-thien-nhien-3.jpg"));
        popularPlaces.add(new PopularPlaces("Đà Nẵng","data:image/jpeg;base64,"));
        popularPlaces.add(new PopularPlaces("TP HCM","https://meta.vn/Data/image/2022/01/13/anh-dep-thien-nhien-3.jpg"));
        popularPlaces.add(new PopularPlaces("TP HCM","https://hdproland.com/media/posts/dien-tich-cac-quan-tai-ho-chi-minh-1.jpg"));

        PopularPlaceAdapter popularPlaceAdapter = new PopularPlaceAdapter(this.getContext(), popularPlaces);

        recyclerView.setLayoutManager(new LinearLayoutManager(getContext(),LinearLayoutManager.HORIZONTAL,false));

        recyclerView.setAdapter(popularPlaceAdapter);

    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}